    public function render()
    {
        $svg = self::$icons[$this->name] ?? throw new \Exception("Icon '{$this->name}' not found.");
        
        $svg = str_replace('<svg', "<svg width='{$this->size}' height='{$this->size}'", $svg);
        
        if ($this->color) {
            $svg = str_replace('currentColor', $this->color, $svg);
        }
        
        if ($this->class) {
            $svg = str_replace('<svg', "<svg class='{$this->class}'", $svg);
        }

        if ($this->fill) {
            $svg = str_replace('fill="none"', "fill=\"{$this->fill}\"", $svg);
        }

        return $svg;
    }
